import React, { Component } from "react";
import styled from "styled-components";

const Main = styled.div`
  background-color: #334c7b;
  width: 100vw;
  height: 100vh;
`;

const Container = styled.div`
  margin: 0 auto;
  width: 24rem;
`;

const Link = styled.a`
  font-size: 14px;
  color: white;
  text-align: center;
  width: 100%;
`;

const Input = styled.input`
  color: white;
  font-size: 16px;
  font-family: sans-serif;
  width: 100%;
  border: none;
  border-bottom: 2px solid white;
  padding-top: 8px;
  padding-bottom: 8px;
  padding-left: 8px;
  appearance: none;
  background-color: transparent;
  margin-bottom: 16px;
`;

const Button = styled.button`
  font-weight: 700;
  color: white;
  appearance: none;
  border: none;
  font-size: 16px;
  width: 100%;
  height: 40px;
  border-radius: 20px;
  background-color: #d03c42;
`;

class App extends Component {
  render() {
    return (
      <div>
        <Main>
          <Container>
            <Input placeholder="Email" />
            <Input placeholder="Password" />
            <Button>Login</Button>
            <Link>Forgot your password?</Link>
          </Container>
        </Main>
      </div>
    );
  }
}

export default App;
